import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  const isNotEmpty = (value) => value.trim() !== "";
  const isEmail = (value) => value.includes("@");

  const BasicForm = (props) => {
    const {
      value: firstNameValue,
      isValid: frstNameIsValid,
      hasError: firstNameInputHasError,
      valueChangeHandler: firstNameChangedHandler,
      inputBlurHandler: firstNameBlurHandler,
      reset: resetFirstName,
    } = useInput(isNotEmpty);
    const {
      value: lastNameValue,
      isValid: lastNameIsValid,
      hasError: lastNameInputHasError,
      valueChangeHandler: lastNameChangedHandler,
      inputBlurHandler: lastNameBlurHandler,
      reset: resetLastName,
    } = useInput(isNotEmpty);
    const {
      value: emailValue,
      isValid: emailIsValid,
      hasError: emailInputHasError,
      valueChangeHandler: emailChangedHandler,
      inputBlurHandler: emailBlurHandler,
      reset: resetEmail,
    } = useInput(isEmail);
  };

  const firstNameClasses = firstNameInputHasError
    ? "form-control invalid"
    : "form-control";

  const lastNameClasses = lastNameInputHasError
    ? "form-control invalid"
    : "form-control";

  const emailClasses = emailInputHasError
    ? "form-control invalid"
    : "form-control";

  let formIsValid = false;
  if (frstNameIsValid && lastNameIsValid && emailIsValid) {
    formIsValid = true;
  }

  const submitHandler = (event) => {
    event.preventDefault();

    if (!enteredNameIsValid && !enteredEmailIsValid) {
      return;
    }

    console.log(enteredName);
    console.log(enteredEmail);

    //nameInputRef.current.value = ""; --> not ideal - directly manipulating with DOM
    resetNameInput();

    resetEmailInput();
  };


  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className={firstNameClasses}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameChangedHandler}
            onBlur={firstNameBlurHandler}
            value={firstNameValue}
          />{" "}
          {firstNameInputHasError && (
            <p className="error-text">Please enter a first name!</p>
          )}
        </div>
        <div className={lastNameClasses}>
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            onChange={lastNameChangedHandler}
            onBlur={lastNameBlurHandler}
            value={lastNameValue}
          />
          {lastNameInputHasError && (
            <p className="error-text">Please enter a valid last name!</p>
          )}
        </div>
      </div>
      <div className={emailClasses}>
        <label htmlFor="name">E-Mail Address</label>
        <input
          type="text"
          id="name"
          onChange={emailChangedHandler}
          onBlur={emailBlurHandler}
          value={emailValue}
        />
        {emailInputHasError && (
          <p className="error-text">Please enter a valid email!</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
