import React, { useCallback, useState } from "react";

import "./App.css";
import DemoOutput from "./components/Demo/DemoOutput";
import Button from "./components/UI/Button/Button";

//every component in which state is executed, component is revalidated/reexecuted
//useCallBack() --> saving function in memory so the comparison with functions can work
function App() {
  const [showParagraph, setShowParagraph] = useState(false);

  console.log("APP RUNNING");

  //useCallback like useState garanties that setShowParagraph will be always the same object --> no need to put setShowParagraph in dependencies
  const toggleParagraphHandler = useCallback(() => {
    setShowParagraph((prevShowParagraph) => !prevShowParagraph);
  }, []); //empty array of dependencies, same object function needs to be reused (never changed object)

  return (
    <div className="app">
      <h1>Hi there!</h1>
      <DemoOutput show={false} />
      <Button onClick={toggleParagraphHandler}>Show paragraph!</Button>
    </div>
  );
}

export default App;
