import { Component, useState } from "react";
import User from "./User";

import classes from "./Users.module.css";

const DUMMY_USERS = [
  { id: "u1", name: "Max" },
  { id: "u2", name: "Manuel" },
  { id: "u3", name: "Julie" },
];

class Users extends Component {
  constructor() {
    super();
    this.state = {
      showUser: true,
      //moreSTate: 'Test'
      //nestetState: {},
      //arrayState: []
    };
  }

  toggleUsersHandler() {
    //this.state.showUser=false; --> NO!
    //this.setState({ showUsers: false }); --> if I have more states, only defined one will be changed OR:
    //this.setState((curState) => !curState); --> use when this state depends on previous state

    //returning object
    this.setState((curState) => {
      return { showUsers: !curState.showUsers };
    });
  }

  render() {
    const usersList = (
      <ul>
        {DUMMY_USERS.map((user) => (
          <User key={user.id} name={user.name} />
        ))}
      </ul>
    );

    //bind(this) --> "this" inside toggleUsersHandler() method is now set to have same context/value when keyword is revaluated (in button)
    <div className={classes.users}>
      <button onClick={this.toggleUsersHandler.bind(this)}>
        {this.state.showUsers ? "Hide" : "Show"} Users
      </button>
      {this.state.showUsers && usersList}
    </div>;
  }
}

