import { Component, useState } from "react";
import User from "./User";

import classes from "./Users.module.css";

const DUMMY_USERS = [
  { id: "u1", name: "Max" },
  { id: "u2", name: "Manuel" },
  { id: "u3", name: "Julie" },
];

class Users extends Component {
  constructor() {
    this.state = {
      showUser: true,
      //moreSTate: 'Test'
      //nestetState: {},
      //arrayState: []
    };
  }

  usersList = (
    <ul>
      {DUMMY_USERS.map((user) => (
        <User key={user.id} name={user.name} />
      ))}
    </ul>
  );

  toggleUsersHandler() {
    //this.state.showUser=false; --> NO!
    this.setState({ showUser: false }); //if I have more states, only defined one will be changed
  }

  render() {
    <div className={classes.users}>
      <button onClick={toggleUsersHandler}>
        {showUsers ? "Hide" : "Show"} Users
      </button>
      {showUsers && usersList}
    </div>;
  }
}

